mapper.select:
select 

from acinfo where 1=1

and Id=#{id}


and siteId=#{siteId}


and portId=#{portId}


and AcBusinessId=#{acBusinessId}


and lagid=#{lagId}


and acStatus=#{acStatus}

SELECT * FROM acinfo WHERE 1=1

	and id = #{id}


	and siteId = #{siteId}


	and portId = #{portId}


	and acBusinessId = #{acBusinessId}


	and lagId = #{lagId}
