mapper.select:
select 

from qosinfo where 1=1
    
       and siteId=#{siteId}
   
   
       and groupid=#{groupId}
   
   
       and qostype=#{qosType}
   
   
       and qosname=#{qosname}
   
   
       and status=#{status}
   
   
       and id=#{id}

select * from qosinfo where groupId in (select qosGroupId from qosrelevance where objType = #{objType} 

and objId in 
  
 #{item}

mapper.update:
update QosInfo set seq = #{seq}, cir = #{cir} , cbs = #{cbs} , eir = #{eir} , ebs = #{ebs}, 
pir = #{pir} , pbs = #{pbs} , strategy = #{strategy} , colorSense = #{colorSence} , status = #{status} 
 where siteId = #{siteId} and cos = #{cos} and qosname = #{qosname} and direction = #{direction}

