mapper.delete:
delete from offlinesitebusi
where 1=1 
 and id=#{id}

mapper.select:
select
from offlinesitebusi t WHERE 1=1 and
#{item.Site_Inst_Id}

    select 
    
    from offlinesitebusi t WHERE 1=1 
     and Id=#{id}
	 and operId=#{operId}	
	 and operType=#{operType}	
	 and siteId=#{siteId}
